import{_ as a,B as e,c as o,o as n,ai as l,G as d}from"./chunks/framework.jrH_H5Hh.js";const m=JSON.parse('{"title":"TCP 和 UDP 的对比","description":"","frontmatter":{},"headers":[],"relativePath":"my-blog/computer-network/TCP-UDP.md","filePath":"my-blog/computer-network/TCP-UDP.md"}'),s={name:"my-blog/computer-network/TCP-UDP.md"};function i(h,t,c,P,u,_){const r=e("GiscusComment");return n(),o("div",null,[t[0]||(t[0]=l('<h1 id="tcp-和-udp-的对比" tabindex="-1">TCP 和 UDP 的对比 <a class="header-anchor" href="#tcp-和-udp-的对比" aria-label="Permalink to &quot;TCP 和 UDP 的对比&quot;">​</a></h1><p>TCP（传输控制协议）和 UDP（用户数据报协议）是传输层的核心协议，其主要区别如下：</p><h2 id="_1-连接方式" tabindex="-1">1. 连接方式 <a class="header-anchor" href="#_1-连接方式" aria-label="Permalink to &quot;1. 连接方式&quot;">​</a></h2><ul><li><strong>TCP</strong>：面向连接，需要通过三次握手建立连接（SYN → SYN-ACK → ACK），传输结束后通过四次挥手释放连接（FIN → ACK → FIN → ACK）。</li><li><strong>UDP</strong>：无连接，直接发送数据报，无需建立或释放连接。</li></ul><h2 id="_2-可靠性" tabindex="-1">2. 可靠性 <a class="header-anchor" href="#_2-可靠性" aria-label="Permalink to &quot;2. 可靠性&quot;">​</a></h2><ul><li><strong>TCP</strong>：通过确认应答（ACK）、超时重传、流量控制和拥塞控制等机制，确保数据传输可靠，保证数据不丢失、不重复且有序。</li><li><strong>UDP</strong>：不保证可靠性，可能出现丢包、乱序或重复。</li></ul><h2 id="_3-传输效率" tabindex="-1">3. 传输效率 <a class="header-anchor" href="#_3-传输效率" aria-label="Permalink to &quot;3. 传输效率&quot;">​</a></h2><ul><li><strong>TCP</strong>：由于需要连接管理和可靠性机制，头部开销较大（至少 20 字节），传输效率相对较低。</li><li><strong>UDP</strong>：头部仅 8 字节，无连接管理，实时性高，适合视频会议、在线游戏等场景。</li></ul><h2 id="_4-数据边界" tabindex="-1">4. 数据边界 <a class="header-anchor" href="#_4-数据边界" aria-label="Permalink to &quot;4. 数据边界&quot;">​</a></h2><ul><li><strong>TCP</strong>：面向字节流，无固定报文边界，应用层需自行处理。</li><li><strong>UDP</strong>：面向报文，保留应用层下发的报文边界。</li></ul><h2 id="_5-应用场景" tabindex="-1">5. 应用场景 <a class="header-anchor" href="#_5-应用场景" aria-label="Permalink to &quot;5. 应用场景&quot;">​</a></h2><ul><li><strong>TCP</strong>：适用于对可靠性要求较高的场景，例如网页浏览、文件传输、电子邮件等。</li><li><strong>UDP</strong>：适用于对实时性要求较高的场景，例如 DNS 查询、视频流、VoIP 等。</li></ul><hr><h2 id="_6-对比总结" tabindex="-1">6. 对比总结 <a class="header-anchor" href="#_6-对比总结" aria-label="Permalink to &quot;6. 对比总结&quot;">​</a></h2><table tabindex="0"><thead><tr><th>特性</th><th>TCP</th><th>UDP</th></tr></thead><tbody><tr><td><strong>连接方式</strong></td><td>面向连接</td><td>无连接</td></tr><tr><td><strong>可靠性</strong></td><td>可靠（确认/重传机制）</td><td>不可靠</td></tr><tr><td><strong>传输效率</strong></td><td>较低（头部开销大）</td><td>较高（头部仅 8 字节）</td></tr><tr><td><strong>数据控制</strong></td><td>有流量控制和拥塞控制</td><td>无控制</td></tr><tr><td><strong>适用场景</strong></td><td>文件传输、网页访问等</td><td>实时视频、语音通话等</td></tr></tbody></table><h2 id="_7-掘金文章" tabindex="-1">7. 掘金文章 <a class="header-anchor" href="#_7-掘金文章" aria-label="Permalink to &quot;7. 掘金文章&quot;">​</a></h2><p><a href="https://juejin.cn/post/7275549394492080188?searchId=2025041715172596B16D7C74DB84DD7487" target="_blank" rel="noreferrer">点击跳转</a></p>',17)),d(r)])}const p=a(s,[["render",i]]);export{m as __pageData,p as default};
